  async findone(req, res, next) {
    let { id } = req.params;
    try {
      const find_one = await userModel.findOne({ _id: id });
      if (!find_one) {
        res.status(200).send({
          data: find_one,
          message: "no user found",
          status: 0,
          error: null,
        });
      } else {
        if (find_one.refered_by) {
          const find_referral = await userModel.findOne({
            referal_id: find_one.refered_by,
          });
          if (find_referral) {
            res.status(200).send({
              data: find_one,
              referal_user: find_referral,
              message: "user data",
              status: 1,
              error: null,
            });
          } else {
            res.status(200).send({
              data: find_one,
              referal_user: null,
              message: "user data",
              status: 1,
              error: null,
            });
          }
        } else {
          res.status(200).send({
            data: find_one,
            referal_user: null,
            message: "user data",
            status: 1,
            error: null,
          });
        }
      }
    } catch (error) {
      res.status(400).send({
        error: error,
        message: "error in finding user",
        status: 0,
        data: null,
      });
    }
  },

